// ğŸ€ Elaborazione dati classifiche Basket San Vincenzo - Versione Regex

const htmlRows = $input.all()[0].json.tableRows || [];
const standings = [];
const teamStats = {};

console.log(`ğŸ” Processando ${htmlRows.length} righe HTML...`);

// Funzione per estrarre testo da tag HTML
function extractTextFromHtml(html) {
  if (!html) return '';
  return html.replace(/<[^>]*>/g, '').trim();
}

// Funzione per estrarre href da tag a
function extractHref(html) {
  if (!html) return '';
  const match = html.match(/href=["']([^"']*)["']/i);
  return match ? match[1] : '';
}

htmlRows.forEach((rowHtml, index) => {
  try {
    if (!rowHtml || typeof rowHtml !== 'string') return;

    // Estrai le celle TD dalla riga
    const tdMatches = rowHtml.match(/<td[^>]*>.*?<\/td>/gi);
    if (!tdMatches || tdMatches.length < 10) return;

    const cells = tdMatches.map(td => extractTextFromHtml(td));

    const positionText = cells[0]?.trim();
    const position = parseInt(positionText) || 0;

    // Salta header e righe non valide
    if (position === 0 || positionText === '#' || positionText === 'Pos' || !positionText) return;

    const teamName = cells[1]?.trim() || '';
    if (!teamName || teamName === 'Squadra' || teamName === 'Team') return;

    // Estrai link squadra
    const teamLink = extractHref(tdMatches[1] || '');
    const teamId = teamLink ? (teamLink.match(/obj=(\d+)/) || [])[1] || '' : '';

    const points = parseInt(cells[2]?.trim()) || 0;
    const pointsPerGame = parseFloat(cells[3]?.trim()) || 0;
    const games = parseInt(cells[4]?.trim()) || 0;
    const wins = parseInt(cells[5]?.trim()) || 0;
    const losses = parseInt(cells[6]?.trim()) || 0;

    // Gestisci percentuale
    let percentage = cells[7]?.trim() || '0';
    if (percentage.startsWith('.')) percentage = '0' + percentage;
    if (percentage === '1') percentage = '1.00';
    percentage = parseFloat(percentage) || 0;

    const streak = cells[8]?.trim() || '-';
    const pointsFor = parseInt(cells[9]?.trim()) || 0;
    const pointsAgainst = parseInt(cells[10]?.trim()) || 0;
    const quality = cells[11]?.trim() || 'n/a';

    const pointsForPerGame = cells[12] ? parseFloat(cells[12].trim()) || 0 : (games > 0 ? pointsFor / games : 0);
    const pointsAgainstPerGame = cells[13] ? parseFloat(cells[13].trim()) || 0 : (games > 0 ? pointsAgainst / games : 0);

    const teamData = {
      position,
      team: teamName,
      teamId,
      teamLink: teamLink ? `https://www.playbasket.it/toscana/${teamLink}` : '',
      points,
      pointsPerGame,
      games,
      wins,
      losses,
      percentage,
      streak,
      pointsFor,
      pointsAgainst,
      quality,
      pointsForPerGame,
      pointsAgainstPerGame
    };

    standings.push(teamData);

    teamStats[teamName] = {
      position,
      points,
      games,
      wins,
      losses,
      winPercentage: percentage,
      pointsFor,
      pointsAgainst,
      pointDifference: pointsFor - pointsAgainst,
      averagePointsFor: pointsForPerGame,
      averagePointsAgainst: pointsAgainstPerGame,
      form: streak,
      efficiency: games > 0 ? (wins / games * 100).toFixed(1) : '0.0'
    };

    console.log(`âœ… Processata: ${teamName} - Pos: ${position} - Punti: ${points}`);

  } catch (error) {
    console.error(`âŒ Errore processando riga ${index}:`, error.message);
    console.error(`âŒ HTML riga:`, rowHtml.substring(0, 100));
  }
});

const sortedStandings = standings.sort((a, b) => a.position - b.position);

const result = {
  standings: sortedStandings,
  teamStats,
  lastUpdate: new Date().toISOString(),
  season: '2025-2026',
  competition: 'Serie C Maschile Toscana',
  conference: 'Conference Nord-Ovest',
  group: 'Girone B',
  totalTeams: sortedStandings.length,
  scrapingInfo: {
    timestamp: new Date().toISOString(),
    source: 'playbasket.it',
    method: 'n8n-automated-regex',
    rowsProcessed: htmlRows.length,
    teamsFound: sortedStandings.length
  }
};

// Trova Basket San Vincenzo
const basketSanVincenzo = sortedStandings.find(team => 
  team.team.toLowerCase().includes('san vincenzo') || 
  team.team.toLowerCase().includes('basket san vincenzo')
);

console.log(`ğŸ† Basket San Vincenzo: ${basketSanVincenzo?.position || 'Non trovato'}Â° posto`);
console.log(`ğŸ“Š Totale squadre processate: ${result.totalTeams}`);
console.log(`ğŸ“‹ Prime 3 righe HTML:`, htmlRows.slice(0, 3));

return { json: result };
