// ğŸ€ Elaborazione dati classifiche Basket San Vincenzo - Versione Ottimizzata

// Funzione per processare le righe HTML della tabella
function processStandings(htmlRows) {
  const standings = [];
  const teamStats = {};

  console.log(`ğŸ” Processando ${htmlRows.length} righe HTML...`);

  htmlRows.forEach((rowHtml, index) => {
    try {
      // Parsing HTML della riga con maggiore tolleranza
      const parser = new DOMParser();
      const doc = parser.parseFromString(`<table><tbody>${rowHtml}</tbody></table>`, 'text/html');
      const cells = doc.querySelectorAll('td');

      // Salta header e righe vuote - controllo piÃ¹ flessibile
      if (cells.length < 10) return;

      const positionText = cells[0]?.textContent?.trim();
      const position = parseInt(positionText) || 0;

      // Salta se non Ã¨ una posizione valida
      if (position === 0 || positionText === '#' || positionText === 'Pos' || !positionText) return;

      // Estrai dati dalla riga con fallback per campi mancanti
      const teamCell = cells[1];
      const teamName = teamCell?.textContent?.trim() || '';

      // Skip se il nome squadra Ã¨ vuoto o Ã¨ un header
      if (!teamName || teamName === 'Squadra' || teamName === 'Team') return;

      const teamLink = teamCell?.querySelector('a')?.getAttribute('href') || '';
      const teamId = teamLink ? teamLink.match(/obj=(\d+)/)?.[1] || '' : '';

      const points = parseInt(cells[2]?.textContent?.trim()) || 0;
      const pointsPerGame = parseFloat(cells[3]?.textContent?.trim()) || 0;
      const games = parseInt(cells[4]?.textContent?.trim()) || 0;
      const wins = parseInt(cells[5]?.textContent?.trim()) || 0;
      const losses = parseInt(cells[6]?.textContent?.trim()) || 0;

      // Gestisci percentuale con maggiore flessibilitÃ 
      let percentage = cells[7]?.textContent?.trim() || '0';
      if (percentage.startsWith('.')) percentage = '0' + percentage;
      if (percentage === '1') percentage = '1.00';
      percentage = parseFloat(percentage) || 0;

      const streak = cells[8]?.textContent?.trim() || '-';
      const pointsFor = parseInt(cells[9]?.textContent?.trim()) || 0;
      const pointsAgainst = parseInt(cells[10]?.textContent?.trim()) || 0;
      const quality = cells[11]?.textContent?.trim() || 'n/a';

      // Gestisci campi opzionali che potrebbero non esserci
      const pointsForPerGame = cells[12] ? parseFloat(cells[12].textContent?.trim()) || 0 : (games > 0 ? pointsFor / games : 0);
      const pointsAgainstPerGame = cells[13] ? parseFloat(cells[13].textContent?.trim()) || 0 : (games > 0 ? pointsAgainst / games : 0);

      // Crea oggetto squadra per classifica
      const teamData = {
        position,
        team: teamName,
        teamId,
        teamLink: teamLink ? `https://www.playbasket.it/toscana/${teamLink}` : '',
        points,
        pointsPerGame,
        games,
        wins,
        losses,
        percentage,
        streak,
        pointsFor,
        pointsAgainst,
        quality,
        pointsForPerGame,
        pointsAgainstPerGame
      };

      standings.push(teamData);

      // Crea statistiche dettagliate per squadra
      teamStats[teamName] = {
        position,
        points,
        games,
        wins,
        losses,
        winPercentage: percentage,
        pointsFor,
        pointsAgainst,
        pointDifference: pointsFor - pointsAgainst,
        averagePointsFor: pointsForPerGame,
        averagePointsAgainst: pointsAgainstPerGame,
        form: streak,
        efficiency: games > 0 ? (wins / games * 100).toFixed(1) : '0.0'
      };

      console.log(`âœ… Processata: ${teamName} - Pos: ${position} - Punti: ${points}`);

    } catch (error) {
      console.error(`âŒ Errore processando riga ${index}:`, error.message);
    }
  });

  // Ordina per posizione e aggiungi metadati
  const sortedStandings = standings.sort((a, b) => a.position - b.position);

  return {
    standings: sortedStandings,
    teamStats,
    lastUpdate: new Date().toISOString(),
    season: '2025-2026',
    competition: 'Serie C Maschile Toscana',
    conference: 'Conference Nord-Ovest',
    group: 'Girone B',
    totalTeams: sortedStandings.length,
    scrapingInfo: {
      timestamp: new Date().toISOString(),
      source: 'playbasket.it',
      method: 'n8n-automated',
      rowsProcessed: htmlRows.length,
      teamsFound: sortedStandings.length
    }
  };
}

// Funzione per confrontare con dati precedenti
function hasSignificantChanges(newData, oldData) {
  if (!oldData || !oldData.standings) return true;

  // Controlla se il numero di squadre Ã¨ cambiato
  if (newData.standings.length !== oldData.standings.length) return true;

  // Controlla se le posizioni sono cambiate
  for (let i = 0; i < newData.standings.length; i++) {
    const newTeam = newData.standings[i];
    const oldTeam = oldData.standings.find(t => t.team === newTeam.team);

    if (!oldTeam) return true;
    if (oldTeam.position !== newTeam.position) return true;
    if (oldTeam.points !== newTeam.points) return true;
    if (oldTeam.games !== newTeam.games) return true;
  }

  return false;
}

// Codice principale N8N
const inputData = $input.all()[0].json;
const htmlRows = inputData.tableRows || [];

console.log(`ğŸš€ Avvio elaborazione - Ricevute ${htmlRows.length} righe`);

const processedData = processStandings(htmlRows);

// Crea struttura dati completa
const fullStats = {
  serieC: processedData,
  under17: {
    standings: [],
    teamStats: {},
    lastUpdate: new Date().toISOString(),
    note: 'Dati non disponibili - concentrato su Serie C'
  },
  under13: {
    standings: [],
    teamStats: {},
    lastUpdate: new Date().toISOString(),
    note: 'Dati non disponibili - concentrato su Serie C'
  },
  lastUpdate: new Date().toISOString(),
  source: 'playbasket.it',
  automation: {
    platform: 'n8n',
    frequency: 'LunedÃ¬ e GiovedÃ¬ ore 8:00',
    nextRun: 'Prossima esecuzione programmata'
  }
};

// Trova posizione Basket San Vincenzo
const basketSanVincenzo = processedData.standings.find(team => 
  team.team.toLowerCase().includes('san vincenzo') || 
  team.team.toLowerCase().includes('basket san vincenzo')
);

// Trova le prime 3 posizioni per il riepilogo
const topThree = processedData.standings.slice(0, 3);

console.log(`ğŸ† Basket San Vincenzo:`, basketSanVincenzo);
console.log(`ğŸ“Š Totale squadre: ${processedData.standings.length}`);
console.log(`ğŸ¥‡ Podio:`, topThree.map(t => `${t.position}Â° ${t.team}`));

// Calcola statistiche aggiuntive
const avgPointsFor = processedData.standings.reduce((sum, team) => sum + team.pointsFor, 0) / processedData.standings.length;
const avgPointsAgainst = processedData.standings.reduce((sum, team) => sum + team.pointsAgainst, 0) / processedData.standings.length;

// Output per i nodi successivi
return [{
  json: {
    serieCStats: processedData,
    fullStats: fullStats,
    basketSanVincenzo: basketSanVincenzo,
    basketSanVincenzoPosition: basketSanVincenzo?.position || 'Non trovato',
    totalTeams: processedData.standings.length,
    lastUpdate: processedData.lastUpdate,
    success: processedData.standings.length > 0,
    topThree: topThree,
    statistics: {
      avgPointsFor: Math.round(avgPointsFor * 10) / 10,
      avgPointsAgainst: Math.round(avgPointsAgainst * 10) / 10,
      totalGames: processedData.standings.reduce((sum, team) => sum + team.games, 0),
      scrapingTimestamp: new Date().toISOString()
    },
    changeDetection: {
      hasChanges: true, // Sempre true per ora, in futuro si puÃ² implementare il confronto
      significantChanges: true
    }
  }
}];

