// Parser HTML completo con SHA dinamico
console.log('üèÄ INIZIO PARSING COMPLETO CON SHA DINAMICO');

// Accesso ai dati HTML (primo input)
const htmlData = items[0];
let html = null;
if (htmlData.data) {
  html = htmlData.data;
} else if (htmlData.json && typeof htmlData.json === 'string') {
  html = htmlData.json;
}

if (!html) {
  console.error('‚ùå HTML non trovato');
  return [{ json: { success: false, error: 'HTML not found' } }];
}

// Accesso ai dati SHA (secondo e terzo input)
const jsonShaData = items[1];
const tsShaData = items[2];
const jsonSha = jsonShaData.json.sha;
const tsSha = tsShaData.json.sha;

console.log('üìã SHA JSON:', jsonSha);
console.log('üìã SHA TS:', tsSha);

// Parsing HTML (codice testato e funzionante)
const standings = [];
const rowRegex = /<tr class='row_standings'>([\s\S]*?)<\/tr>/g;
let rowMatch;

while ((rowMatch = rowRegex.exec(html)) !== null) {
  const rowContent = rowMatch[1];

  try {
    const posMatch = /<td[^>]*class='colfrozen'>(\d+)<\/td>/.exec(rowContent);
    if (!posMatch) continue;
    const position = parseInt(posMatch[1]);

    const teamMatch = /<td class="sq colfrozen"><a href="club\.php\?obj=(\d+)&action=view&eid=\d+">([^<]+)<\/a>/.exec(rowContent);
    if (!teamMatch) continue;

    const teamId = teamMatch[1];
    const teamName = teamMatch[2].trim();
    const teamLink = `https://www.playbasket.it/toscana/club.php?obj=${teamId}&action=view&eid=4`;

    const pointsMatch = /<td[^>]*class='highlighted_data'>(\d+)<\/td>/.exec(rowContent);
    const points = pointsMatch ? parseInt(pointsMatch[1]) : 0;

    const pointsPerGameMatch = /<td[^>]*class='divisore'>([\d.]+)<\/td>/.exec(rowContent);
    const pointsPerGame = pointsPerGameMatch ? parseFloat(pointsPerGameMatch[1]) : 0;

    const simpleTdRegex = /<td title='[^']*'>([^<]+)<\/td>/g;
    const values = [];
    let tdMatch;

    while ((tdMatch = simpleTdRegex.exec(rowContent)) !== null) {
      values.push(tdMatch[1].trim());
    }

    const games = values[0] ? parseInt(values[0]) : 0;
    const wins = values[1] ? parseInt(values[1]) : 0;
    const losses = values[2] ? parseInt(values[2]) : 0;
    const percentage = values[3] ? parseFloat(values[3]) : 0;

    const streakMatch = /<td[^>]*class='[^']*streak[^']*'[^>]*>([^<]+)<\/td>/.exec(rowContent);
    const streak = streakMatch ? streakMatch[1].trim() : '-';

    const pointsFor = values[5] ? parseInt(values[5]) : 0;
    const pointsAgainst = values[6] ? parseInt(values[6]) : 0;
    const quality = values[7] ? parseFloat(values[7]) : 0;
    const pointsForPerGame = values[8] ? parseFloat(values[8]) : 0;
    const pointsAgainstPerGame = values[9] ? parseFloat(values[9]) : 0;

    standings.push({
      position, team: teamName, teamId, teamLink, points, pointsPerGame,
      games, wins, losses, percentage, streak, pointsFor, pointsAgainst,
      quality: quality.toFixed(2), pointsForPerGame, pointsAgainstPerGame
    });

  } catch (error) {
    console.error('‚ùå Errore processando riga:', error);
  }
}

// Verifica qualit√† dati
if (standings.length < 10) {
  console.error(`‚ùå Dati insufficienti: solo ${standings.length} squadre`);
  return [{ json: { success: false, error: `Insufficient data: ${standings.length} teams` } }];
}

// Creazione dati completi
const teamStats = {};
standings.forEach(team => {
  teamStats[team.team] = {
    position: team.position, points: team.points, games: team.games,
    wins: team.wins, losses: team.losses, winPercentage: team.percentage,
    pointsFor: team.pointsFor, pointsAgainst: team.pointsAgainst,
    pointDifference: team.pointsFor - team.pointsAgainst,
    averagePointsFor: team.pointsForPerGame,
    averagePointsAgainst: team.pointsAgainstPerGame
  };
});

const statsData = {
  standings, teamStats,
  lastUpdate: new Date().toISOString(),
  season: '2025-2026',
  competition: 'Serie C Maschile Toscana',
  conference: 'Conference Nord-Ovest',
  group: 'Girone B',
  totalTeams: standings.length,
  source: 'playbasket.it',
  status: 'active'
};

// Preparazione dati per GitHub
const jsonContent = JSON.stringify(statsData, null, 2);
const jsonBase64 = Buffer.from(jsonContent).toString('base64');

// Preparazione contenuto TypeScript
const tsContent = `// Dati delle statistiche Serie C
// Questo file viene importato direttamente dai componenti
// Aggiornato automaticamente: ${new Date().toISOString()}

export const serieCStatsData = ${JSON.stringify(statsData, null, 2)};`;
const tsBase64 = Buffer.from(tsContent).toString('base64');

const result = {
  success: true,
  teamsCount: standings.length,
  jsonData: {
    message: `Aggiornamento automatico serie-c-stats.json - ${new Date().toISOString()}`,
    content: jsonBase64,
    sha: jsonSha
  },
  tsData: {
    message: `Aggiornamento automatico stats-data.ts - ${new Date().toISOString()}`,
    content: tsBase64,
    sha: tsSha
  }
};

console.log(`üéâ Parsing completato: ${standings.length} squadre, JSON SHA: ${jsonSha}, TS SHA: ${tsSha}`);
return [{ json: result }];

// Messaggio di successo finale completo
const jsonUpdate = items[0];
const tsUpdate = items[1];
const netlifyDeploy = items[2];

const jsonSuccess = jsonUpdate.json && jsonUpdate.json.content;
const tsSuccess = tsUpdate.json && tsUpdate.json.content;
const netlifySuccess = netlifyDeploy.json || netlifyDeploy.statusCode === 200;

const message = `üéâ Aggiornamento automatico completato!

üìä Risultati:
- File JSON: ${jsonSuccess ? '‚úÖ Aggiornato' : '‚ùå Errore'}
- File TypeScript: ${tsSuccess ? '‚úÖ Aggiornato' : '‚ùå Errore'}
- Deploy Netlify: ${netlifySuccess ? '‚úÖ Triggerato' : '‚ùå Errore'}

üìù Commit Details:
- JSON Commit: ${jsonUpdate.json?.commit?.sha || 'N/A'}
- TS Commit: ${tsUpdate.json?.commit?.sha || 'N/A'}

‚è∞ Timestamp: ${new Date().toISOString()}

üéØ Prossimo aggiornamento: Luned√¨/Gioved√¨ alle 8:00 AM`;

console.log(message);

return [{
  json: {
    success: jsonSuccess && tsSuccess,
    message: message,
    details: {
      jsonUpdate: jsonSuccess,
      tsUpdate: tsSuccess,
      netlifyDeploy: netlifySuccess,
      jsonCommit: jsonUpdate.json?.commit?.sha,
      tsCommit: tsUpdate.json?.commit?.sha
    },
    timestamp: new Date().toISOString()
  }
}];

// Gestione errore completa
console.error('‚ùå Errore durante l\'esecuzione del workflow');
console.error('Possibili cause: scraping fallito, dati insufficienti, o errore di parsing');

const errorDetails = items[0]?.json || { error: 'Unknown error' };

const message = `‚ùå Workflow fallito

üîç Dettagli errore:
${JSON.stringify(errorDetails, null, 2)}

‚è∞ Timestamp: ${new Date().toISOString()}

üîÑ Il prossimo tentativo sar√†: Luned√¨/Gioved√¨ alle 8:00 AM`;

console.error(message);

return [{
  json: {
    success: false,
    error: message,
    errorDetails: errorDetails,
    timestamp: new Date().toISOString()
  }
}];
