// Parser HTML corretto per PlayBasket.it - VERSIONE FINALE
console.log('üèÄ INIZIO PARSING PLAYBASKET - VERSIONE FINALE');

if (!items || items.length === 0) {
  console.error('‚ùå Nessun dato ricevuto');
  return [{ json: { success: false, error: 'No input data' } }];
}

// Accesso corretto ai dati HTML
let html = null;
if (items[0].data) {
  html = items[0].data;
} else if (items[0].json && typeof items[0].json === 'string') {
  html = items[0].json;
} else if (items[0].json && items[0].json.data) {
  html = items[0].json.data;
}

if (!html) {
  console.error('‚ùå HTML non trovato');
  return [{ json: { success: false, error: 'HTML not found' } }];
}

console.log('üìä Lunghezza HTML:', html.length);

// Estrazione delle righe della classifica
const standings = [];
const rowRegex = /<tr class='row_standings'>([\s\S]*?)<\/tr>/g;
let rowMatch;

while ((rowMatch = rowRegex.exec(html)) !== null) {
  const rowContent = rowMatch[1];

  try {
    // Estrazione posizione
    const posMatch = /<td[^>]*class='colfrozen'>(\d+)<\/td>/.exec(rowContent);
    if (!posMatch) continue;
    const position = parseInt(posMatch[1]);

    // Estrazione squadra e ID
    const teamMatch = /<td class="sq colfrozen"><a href="club\.php\?obj=(\d+)&action=view&eid=\d+">([^<]+)<\/a>/.exec(rowContent);
    if (!teamMatch) continue;

    const teamId = teamMatch[1];
    const teamName = teamMatch[2].trim();
    const teamLink = `https://www.playbasket.it/toscana/club.php?obj=${teamId}&action=view&eid=4`;

    // Estrazione punti in classifica (highlighted_data)
    const pointsMatch = /<td[^>]*class='highlighted_data'>(\d+)<\/td>/.exec(rowContent);
    const points = pointsMatch ? parseInt(pointsMatch[1]) : 0;

    // Estrazione punti per partita (divisore)
    const pointsPerGameMatch = /<td[^>]*class='divisore'>([\d.]+)<\/td>/.exec(rowContent);
    const pointsPerGame = pointsPerGameMatch ? parseFloat(pointsPerGameMatch[1]) : 0;

    // Estrazione altri dati nell'ordine
    const simpleTdRegex = /<td title='[^']*'>([^<]+)<\/td>/g;
    const values = [];
    let tdMatch;

    while ((tdMatch = simpleTdRegex.exec(rowContent)) !== null) {
      values.push(tdMatch[1].trim());
    }

    const games = values[0] ? parseInt(values[0]) : 0;
    const wins = values[1] ? parseInt(values[1]) : 0;
    const losses = values[2] ? parseInt(values[2]) : 0;
    const percentage = values[3] ? parseFloat(values[3]) : 0;

    // Estrazione striscia
    const streakMatch = /<td[^>]*class='[^']*streak[^']*'[^>]*>([^<]+)<\/td>/.exec(rowContent);
    const streak = streakMatch ? streakMatch[1].trim() : '-';

    const pointsFor = values[5] ? parseInt(values[5]) : 0;
    const pointsAgainst = values[6] ? parseInt(values[6]) : 0;
    const quality = values[7] ? parseFloat(values[7]) : 0;
    const pointsForPerGame = values[8] ? parseFloat(values[8]) : 0;
    const pointsAgainstPerGame = values[9] ? parseFloat(values[9]) : 0;

    const teamData = {
      position: position,
      team: teamName,
      teamId: teamId,
      teamLink: teamLink,
      points: points,
      pointsPerGame: pointsPerGame,
      games: games,
      wins: wins,
      losses: losses,
      percentage: percentage,
      streak: streak,
      pointsFor: pointsFor,
      pointsAgainst: pointsAgainst,
      quality: quality.toFixed(2),
      pointsForPerGame: pointsForPerGame,
      pointsAgainstPerGame: pointsAgainstPerGame
    };

    standings.push(teamData);

  } catch (error) {
    console.error('‚ùå Errore processando riga:', error);
  }
}

// Creazione statistiche per squadra
const teamStats = {};
standings.forEach(team => {
  teamStats[team.team] = {
    position: team.position,
    points: team.points,
    games: team.games,
    wins: team.wins,
    losses: team.losses,
    winPercentage: team.percentage,
    pointsFor: team.pointsFor,
    pointsAgainst: team.pointsAgainst,
    pointDifference: team.pointsFor - team.pointsAgainst,
    averagePointsFor: team.pointsForPerGame,
    averagePointsAgainst: team.pointsAgainstPerGame
  };
});

const result = {
  standings: standings,
  teamStats: teamStats,
  lastUpdate: new Date().toISOString(),
  season: '2025-2026',
  competition: 'Serie C Maschile Toscana',
  conference: 'Conference Nord-Ovest',
  group: 'Girone B',
  totalTeams: standings.length,
  source: 'playbasket.it',
  status: 'active'
};

console.log(`üéâ Parsing completato: ${standings.length} squadre processate`);
return [{ json: result }];

// Preparazione dati per GitHub - JSON
const statsData = items[0].json;

// Creazione contenuto Base64 per il file JSON
const jsonContent = JSON.stringify(statsData, null, 2);
const base64Content = Buffer.from(jsonContent).toString('base64');

// SHA del file corrente
const currentSha = items[1].json.sha;

return [{
  json: {
    message: `Aggiornamento automatico statistiche Serie C - ${new Date().toISOString()}`,
    content: base64Content,
    sha: currentSha
  }
}];

// Preparazione dati per GitHub - TypeScript
const statsData = items[0].json;

// Creazione contenuto TypeScript
const tsContent = `// Dati delle statistiche Serie C
// Questo file viene importato direttamente dai componenti
// Aggiornato automaticamente: ${new Date().toISOString()}

export const serieCStatsData = ${JSON.stringify(statsData, null, 2)};`;

const base64Content = Buffer.from(tsContent).toString('base64');

// SHA del file corrente
const currentSha = items[2].json.sha;

return [{
  json: {
    message: `Aggiornamento automatico stats-data.ts - ${new Date().toISOString()}`,
    content: base64Content,
    sha: currentSha
  }
}];

// Messaggio di successo finale
const jsonUpdate = items[0];
const tsUpdate = items[1];
const netlifyDeploy = items[2];

const message = `üéâ Aggiornamento completato con successo!

üìä Statistiche aggiornate:
- File JSON: ${jsonUpdate.json ? '‚úÖ Aggiornato' : '‚ùå Errore'}
- File TypeScript: ${tsUpdate.json ? '‚úÖ Aggiornato' : '‚ùå Errore'}
- Deploy Netlify: ${netlifyDeploy.json ? '‚úÖ Triggerato' : '‚ùå Errore'}

‚è∞ Timestamp: ${new Date().toISOString()}`;

console.log(message);

return [{
  json: {
    success: true,
    message: message,
    timestamp: new Date().toISOString()
  }
}];

// Gestione errore - dati non validi
console.error('‚ùå Errore durante l\'esecuzione del workflow');
console.error('Dati non validi o scraping fallito');

return [{
  json: {
    success: false,
    error: 'Dati non validi o scraping fallito',
    timestamp: new Date().toISOString()
  }
}];
