// Analizza ogni proprietÃ 
const analysis = {};

for (const key of Object.keys(item)) {
  const value = item[key];
  analysis[key] = {
    type: typeof value,
    isArray: Array.isArray(value),
    length: value?.length || 'N/A',
    keys: typeof value === 'object' && value !== null ? Object.keys(value) : 'N/A',
    preview: typeof value === 'string' ? value.substring(0, 100) + '...' : 
             typeof value === 'object' ? JSON.stringify(value).substring(0, 100) + '...' : 
             String(value)
  };

  console.log(`ğŸ“Š ${key}:`, analysis[key]);
}

// Cerca specificamente i dati HTML
let htmlFound = false;
let htmlLocation = null;
let htmlPreview = null;

if (typeof item.json === 'string' && item.json.includes('<html')) {
  htmlFound = true;
  htmlLocation = 'item.json';
  htmlPreview = item.json.substring(0, 200);
} else if (typeof item.data === 'string' && item.data.includes('<html')) {
  htmlFound = true;
  htmlLocation = 'item.data';
  htmlPreview = item.data.substring(0, 200);
} else if (item.json && typeof item.json.data === 'string' && item.json.data.includes('<html')) {
  htmlFound = true;
  htmlLocation = 'item.json.data';
  htmlPreview = item.json.data.substring(0, 200);
}

const result = {
  success: true,
  itemType: typeof item,
  itemKeys: Object.keys(item),
  analysis: analysis,
  htmlFound: htmlFound,
  htmlLocation: htmlLocation,
  htmlPreview: htmlPreview
};

