// Debug del parsing HTML - VERSIONE CORRETTA
console.log('🔍 INIZIO DEBUG PARSING');

if (!items || items.length === 0) {
  console.error('❌ Nessun dato ricevuto');
  return [{ json: { success: false, error: 'No input data' } }];
}

console.log('📊 Struttura item ricevuto:', Object.keys(items[0]));
console.log('📊 Tipo di dati:', typeof items[0]);

// Prova diversi modi per accedere ai dati HTML
let html = null;

// Metodo 1: items[0].json (per responseFormat: json)
if (items[0].json && typeof items[0].json === 'string') {
  html = items[0].json;
  console.log('✅ HTML trovato in items[0].json (string)');
} else if (items[0].json && items[0].json.data) {
  html = items[0].json.data;
  console.log('✅ HTML trovato in items[0].json.data');
}

// Metodo 2: items[0].data (per responseFormat: string)
if (!html && items[0].data) {
  html = items[0].data;
  console.log('✅ HTML trovato in items[0].data');
}

// Metodo 3: items[0].binary (per dati binari)
if (!html && items[0].binary && items[0].binary.data) {
  html = Buffer.from(items[0].binary.data, 'base64').toString();
  console.log('✅ HTML trovato in items[0].binary.data');
}

// Metodo 4: accesso diretto
if (!html && typeof items[0] === 'string') {
  html = items[0];
  console.log('✅ HTML è direttamente items[0]');
}

if (!html) {
  console.error('❌ HTML non trovato in nessuna struttura');
  console.log('📊 Struttura completa item:', JSON.stringify(items[0], null, 2));
  return [{ json: { success: false, error: 'HTML not found in any structure', itemStructure: Object.keys(items[0]) } }];
}

console.log('📊 Lunghezza HTML ricevuto:', html.length);
console.log('📊 Primi 500 caratteri HTML:', html.substring(0, 500));

// Test 1: Verifica presenza tabella
const tableTest = html.includes('league_standings_ranking stats');
console.log('🔍 Test tabella presente:', tableTest);

// Test 2: Verifica presenza righe
const rowTest = html.includes('row_standings');
console.log('🔍 Test righe presenti:', rowTest);

// Test 3: Conta le righe
const rowMatches = html.match(/row_standings/g);
console.log('🔍 Numero righe trovate:', rowMatches ? rowMatches.length : 0);

// Test 4: Estrai prima riga per debug
const firstRowRegex = /<tr[^>]*class=["']row_standings["'][^>]*>([\s\S]*?)<\/tr>/;
const firstRowMatch = firstRowRegex.exec(html);
if (firstRowMatch) {
  console.log('🔍 Prima riga trovata, lunghezza contenuto:', firstRowMatch[1].length);
  console.log('🔍 Primi 200 caratteri della prima riga:', firstRowMatch[1].substring(0, 200));
} else {
  console.log('❌ Prima riga NON trovata');
}

// Test 5: Prova regex più semplice
const simpleRowRegex = /<tr class='row_standings'>([\s\S]*?)<\/tr>/g;
let simpleRowMatch;
let simpleRowCount = 0;
while ((simpleRowMatch = simpleRowRegex.exec(html)) !== null && simpleRowCount < 3) {
  simpleRowCount++;
  console.log(`🔍 Riga semplice ${simpleRowCount} trovata, lunghezza:`, simpleRowMatch[1].length);

  // Test estrazione posizione
  const posMatch = /<td[^>]*class=["']colfrozen["']>(\d+)<\/td>/.exec(simpleRowMatch[1]);
  console.log(`🔍 Posizione riga ${simpleRowCount}:`, posMatch ? posMatch[1] : 'NON TROVATA');

  // Test estrazione squadra
  const teamMatch = /<td[^>]*class="sq colfrozen"><a[^>]*href="club\.php\?obj=(\d+)&action=view&eid=\d+">([^<]+)<\/a>/.exec(simpleRowMatch[1]);
  console.log(`🔍 Squadra riga ${simpleRowCount}:`, teamMatch ? teamMatch[2] : 'NON TROVATA');

  // Test estrazione punti
  const pointsMatch = /<td[^>]*class=["']highlighted_data["']>(\d+)<\/td>/.exec(simpleRowMatch[1]);
  console.log(`🔍 Punti riga ${simpleRowCount}:`, pointsMatch ? pointsMatch[1] : 'NON TROVATI');
}

// Test 6: Verifica struttura HTML più ampia
const tableStartIndex = html.indexOf('<table');
const tableEndIndex = html.indexOf('</table>');
console.log('🔍 Indice inizio tabella:', tableStartIndex);
console.log('🔍 Indice fine tabella:', tableEndIndex);

if (tableStartIndex !== -1 && tableEndIndex !== -1) {
  const tableContent = html.substring(tableStartIndex, tableEndIndex + 8);
  console.log('🔍 Lunghezza contenuto tabella:', tableContent.length);

  // Conta tutte le righe tr nella tabella
  const allRowMatches = tableContent.match(/<tr[^>]*>/g);
  console.log('🔍 Numero totale righe <tr> nella tabella:', allRowMatches ? allRowMatches.length : 0);
}

// Risultato debug
const debugResult = {
  success: simpleRowCount > 0,
  message: `Debug completato: ${simpleRowCount} righe processate`,
  debug: {
    htmlLength: html.length,
    tablePresent: tableTest,
    rowsPresent: rowTest,
    rowCount: rowMatches ? rowMatches.length : 0,
    processedRows: simpleRowCount,
    firstRowFound: !!firstRowMatch,
    tableStartIndex: tableStartIndex,
    tableEndIndex: tableEndIndex,
    dataAccessMethod: html === items[0].json ? 'items[0].json' : 
                     html === items[0].data ? 'items[0].data' : 
                     html === items[0].json?.data ? 'items[0].json.data' : 'other'
  }
};

console.log('🎉 Debug completato:', JSON.stringify(debugResult, null, 2));
return [{ json: debugResult }];
