// Scraping delle statistiche dal sito PlayBasket
// Debug dell'input
console.log('ğŸ” Debug input items:', JSON.stringify(items, null, 2));
console.log('ğŸ“Š Items length:', items.length);

if (!items || items.length === 0) {
  console.error('âŒ Nessun dato ricevuto dal nodo precedente');
  return [{ json: { success: false, error: 'No input data', message: 'Nessun dato ricevuto' } }];
}

// Prova diversi modi per accedere ai dati HTML
let html;
if (items[0].json && items[0].json.data) {
  html = items[0].json.data;
  console.log('âœ… Dati trovati in items[0].json.data');
} else if (items[0].json && typeof items[0].json === 'string') {
  html = items[0].json;
  console.log('âœ… Dati trovati in items[0].json (string)');
} else if (items[0].binary && items[0].binary.data) {
  html = items[0].binary.data.toString();
  console.log('âœ… Dati trovati in items[0].binary.data');
} else {
  console.error('âŒ Struttura dati non riconosciuta:', Object.keys(items[0]));
  return [{ json: { success: false, error: 'Invalid data structure', message: 'Struttura dati non valida', debug: items[0] } }];
}

if (!html || html.length === 0) {
  console.error('âŒ HTML vuoto o non valido');
  return [{ json: { success: false, error: 'Empty HTML', message: 'HTML vuoto' } }];
}

const standings = [];
const teamStats = {};
const season = "2025-2026";
const competition = "Serie C Maschile Toscana";

console.log('ğŸ” Inizio parsing HTML...');
console.log('ğŸ“Š Lunghezza HTML:', html.length);

// Regex corretta per trovare le tabelle
const tableRegex = /<table[^>]*class=["']league_standings_ranking stats["'][^>]*>([\s\S]*?)<\/table>/g;
let tableMatch;
let tableCount = 0;

while ((tableMatch = tableRegex.exec(html)) !== null) {
  tableCount++;
  const tableContent = tableMatch[1];
  console.log(`ğŸ“‹ Trovata tabella ${tableCount}`);

  // Regex per trovare le righe della tabella
  const rowRegex = /<tr[^>]*class=["']row_standings["'][^>]*>([\s\S]*?)<\/tr>/g;
  let rowMatch;
  let rowCount = 0;

  while ((rowMatch = rowRegex.exec(tableContent)) !== null) {
    rowCount++;
    const rowContent = rowMatch[1];

    // Estrazione dei dati con regex piÃ¹ robuste
    const positionMatch = /<td[^>]*class=["']colfrozen["']>(\d+)<\/td>/.exec(rowContent);
    const teamNameMatch = /<td[^>]*class="sq colfrozen"><a[^>]*href="club\.php\?obj=(\d+)&action=view&eid=\d+">([^<]+)<\/a>/.exec(rowContent);
    const teamLinkMatch = /<td[^>]*class="sq colfrozen"><a[^>]*href="(club\.php\?obj=\d+&action=view&eid=\d+)">/.exec(rowContent);
    const pointsMatch = /<td[^>]*class=["']highlighted_data["']>(\d+)<\/td>/.exec(rowContent);
    const gamesMatch = /<td[^>]*title=["']Partite giocate["']>(\d+)<\/td>/.exec(rowContent);
    const winsMatch = /<td[^>]*title=["']Partite vinte["']>(\d+)<\/td>/.exec(rowContent);
    const lossesMatch = /<td[^>]*title=["']Partite perse["']>(\d+)<\/td>/.exec(rowContent);
    const percentageMatch = /<td[^>]*title=["']Percentuale vittorie \(Percentage\)["']>([\d.]+)<\/td>/.exec(rowContent);
    const streakMatch = /<td[^>]*class=["']divisore streak["']>(?:<span[^>]*>.*?<\/span>)?([^<]+)<\/td>/.exec(rowContent);
    const pointsForMatch = /<td[^>]*title=["']Punti realizzati["']>(\d+)<\/td>/.exec(rowContent);
    const pointsAgainstMatch = /<td[^>]*title=["']Punti subiti["']>(\d+)<\/td>/.exec(rowContent);
    const qualityMatch = /<td[^>]*title=["']Quoziente punti \| Punti realizzati per ogni canestro subito["']>([\d.\/na]+)<\/td>/.exec(rowContent);
    const pointsForPerGameMatch = /<td[^>]*title=["']Punti realizzati di media a gara["']>([\d.]+)<\/td>/.exec(rowContent);
    const pointsAgainstPerGameMatch = /<td[^>]*title=["']Punti subiti di media a gara["']>([\d.]+)<\/td>/.exec(rowContent);

    if (positionMatch && teamNameMatch && pointsMatch) {
      const teamName = teamNameMatch[2].trim();
      const teamId = teamNameMatch[1];
      const teamLink = teamLinkMatch ? `https://www.playbasket.it/toscana/${teamLinkMatch[1]}` : '';
      const position = parseInt(positionMatch[1], 10);
      const points = parseInt(pointsMatch[1], 10);
      const games = gamesMatch ? parseInt(gamesMatch[1], 10) : 0;
      const wins = winsMatch ? parseInt(winsMatch[1], 10) : 0;
      const losses = lossesMatch ? parseInt(lossesMatch[1], 10) : 0;
      const percentage = percentageMatch ? parseFloat(percentageMatch[1]) : 0;
      const streak = streakMatch ? streakMatch[1].trim() : '-';
      const pointsFor = pointsForMatch ? parseInt(pointsForMatch[1], 10) : 0;
      const pointsAgainst = pointsAgainstMatch ? parseInt(pointsAgainstMatch[1], 10) : 0;
      const quality = qualityMatch ? qualityMatch[1].trim() : 'n/a';
      const pointsForPerGame = pointsForPerGameMatch ? parseFloat(pointsForPerGameMatch[1]) : 0;
      const pointsAgainstPerGame = pointsAgainstPerGameMatch ? parseFloat(pointsAgainstPerGameMatch[1]) : 0;

      const teamData = {
        position,
        team: teamName,
        teamId,
        teamLink,
        points,
        pointsPerGame: games > 0 ? (points / games) : 0,
        games,
        wins,
        losses,
        percentage,
        streak,
        pointsFor,
        pointsAgainst,
        quality,
        pointsForPerGame,
        pointsAgainstPerGame,
      };

      standings.push(teamData);

      teamStats[teamName] = {
        position,
        points,
        games,
        wins,
        losses,
        winPercentage: percentage,
        pointsFor,
        pointsAgainst,
        pointDifference: pointsFor - pointsAgainst,
        averagePointsFor: pointsForPerGame,
        averagePointsAgainst: pointsAgainstPerGame,
      };

      console.log(`âœ… Squadra ${position}: ${teamName} - ${points} punti`);
    }
  }
  console.log(`ğŸ“Š Trovate ${rowCount} righe nella tabella ${tableCount}`);
}

// Trova i dati specifici del Basket San Vincenzo
const basketSanVincenzo = standings.find(team => 
  team.team.toLowerCase().includes('basket san vincenzo') || 
  team.team.toLowerCase().includes('san vincenzo')
);

const result = {
  success: standings.length > 0,
  message: standings.length > 0 ? 'Scraping completato con successo' : 'Nessun dato trovato',
  standings,
  teamStats,
  lastUpdate: new Date().toISOString(),
  season,
  competition,
  conference: "Conference Nord-Ovest",
  group: "Girone B",
  totalTeams: standings.length,
};

// Aggiungi dati specifici del Basket San Vincenzo se trovati
if (basketSanVincenzo) {
  result.basketSanVincenzo = basketSanVincenzo;
  result.basketSanVincenzoPosition = basketSanVincenzo.position;
  console.log(`ğŸ€ Basket San Vincenzo trovato in posizione ${basketSanVincenzo.position}`);
}

console.log(`ğŸ‰ Scraping completato: ${standings.length} squadre trovate`);

return [{ json: result }];

// Prepara i dati per l'upload su GitHub nel formato corretto per serie-c-stats.json
// Debug dell'input
console.log('ğŸ”§ Debug input items:', items.length);
if (items.length < 2) {
  console.error('âŒ Dati insufficienti ricevuti');
  return [{ json: { error: 'Insufficient input data' } }];
}

const processData = items[0].json;
const currentStats = items[1].json;

console.log('ğŸ”§ Preparazione dati per GitHub...');
console.log('ğŸ“Š Squadre processate:', processData.totalTeams);
console.log('ğŸ“‹ SHA file corrente:', currentStats.sha);

// Crea la struttura dati corretta per serie-c-stats.json (NON stats-database.json)
const githubData = {
  "standings": processData.standings,
  "teamStats": processData.teamStats,
  "lastUpdate": processData.lastUpdate,
  "season": processData.season,
  "competition": processData.competition,
  "conference": processData.conference,
  "group": processData.group,
  "totalTeams": processData.totalTeams,
  "source": "playbasket.it",
  "scrapedBy": "n8n-workflow"
};

// Aggiungi dati specifici del Basket San Vincenzo se presenti
if (processData.basketSanVincenzo) {
  githubData.basketSanVincenzo = processData.basketSanVincenzo;
  githubData.basketSanVincenzoPosition = processData.basketSanVincenzoPosition;
}

// Converti in JSON string con formattazione
const jsonString = JSON.stringify(githubData, null, 2);

// Converti in Base64
const base64Content = Buffer.from(jsonString, 'utf8').toString('base64');

// Messaggio di commit
const commitMessage = `ğŸ“Š Aggiornamento statistiche Serie C - ${processData.totalTeams} squadre - ${new Date().toLocaleString('it-IT')}`;

// Debug
console.log('ğŸ“ Lunghezza JSON:', jsonString.length);
console.log('ğŸ” Lunghezza Base64:', base64Content.length);
console.log('ğŸ’¾ Messaggio commit:', commitMessage);
console.log('ğŸ“ File target: src/data/stats/serie-c-stats.json');

if (processData.basketSanVincenzo) {
  console.log(`ğŸ€ Basket San Vincenzo: posizione ${processData.basketSanVincenzo.position}`);
}

return [{
  json: {
    content: base64Content,
    message: commitMessage,
    sha: currentStats.sha,
    branch: "master",
    debug: {
      jsonLength: jsonString.length,
      base64Length: base64Content.length,
      teamsCount: processData.totalTeams,
      basketSanVincenzoPosition: processData.basketSanVincenzoPosition || null,
      targetFile: 'src/data/stats/serie-c-stats.json'
    }
  }
}];

// Verifica e debug del deploy Netlify
const netlifyResponse = items[0].json;

console.log('ğŸš€ Netlify Response completa:', JSON.stringify(netlifyResponse, null, 2));
console.log('ğŸ“‹ Deploy ID:', netlifyResponse.id);
console.log('ğŸ—ï¸ Site ID:', netlifyResponse.site_id);
console.log('ğŸ“… Created at:', netlifyResponse.created_at);
console.log('ğŸŒ¿ Branch:', netlifyResponse.branch);
console.log('ğŸ“ Title:', netlifyResponse.title);

// Verifica se il deploy Ã¨ stato triggerato correttamente
if (netlifyResponse.id && netlifyResponse.site_id) {
  console.log('âœ… Deploy triggerato con successo');

  return [{ 
    json: { 
      success: true, 
      deployId: netlifyResponse.id,
      siteId: netlifyResponse.site_id,
      deployUrl: `https://app.netlify.com/sites/${netlifyResponse.site_id}/deploys`,
      message: 'Deploy Netlify triggerato con successo',
      timestamp: netlifyResponse.created_at,
      branch: netlifyResponse.branch,
      buildHookTitle: netlifyResponse.title
    } 
  }];
} else {
  console.log('âŒ Deploy non triggerato correttamente');
  console.log('ğŸ” Response keys:', Object.keys(netlifyResponse));
  return [{ 
    json: { 
      success: false, 
      error: 'Deploy failed', 
      message: 'Errore nel trigger del deploy Netlify',
      response: netlifyResponse,
      troubleshoot: 'Controlla Build Hook su Netlify Dashboard'
    } 
  }];
}

// Risultato finale del workflow
console.log('ğŸŠ Debug input items:', items.length);
console.log('ğŸ“Š Items received:', JSON.stringify(items, null, 2));

// Gestisci diversi scenari di input
let githubResponse = null;
let netlifyResponse = null;

if (items.length >= 2) {
  // Scenario normale: GitHub + Netlify
  githubResponse = items[0].json;
  netlifyResponse = items[1].json;
} else if (items.length === 1) {
  // Solo un input - determina se Ã¨ GitHub o Netlify
  const singleInput = items[0].json;
  if (singleInput.commit) {
    // Ãˆ una risposta GitHub
    githubResponse = singleInput;
  } else if (singleInput.deployId || singleInput.siteId) {
    // Ãˆ una risposta Netlify (dopo verifica)
    netlifyResponse = singleInput;
  }
}

const result = {
  success: true,
  timestamp: new Date().toISOString(),
  message: 'ğŸ‰ Workflow completato con successo!',
  execution: {
    triggeredBy: 'cron',
    schedule: 'LunedÃ¬ e GiovedÃ¬ alle 8:00',
    nextRun: 'Prossima esecuzione programmata',
    targetFile: 'src/data/stats/serie-c-stats.json'
  },
  steps: {
    scraping: 'âœ… Dati scrapati da PlayBasket',
    github: githubResponse ? 'âœ… File serie-c-stats.json aggiornato su GitHub' : 'âš ï¸ GitHub non processato',
    netlify: netlifyResponse?.success ? 'âœ… Deploy Netlify triggerato' : 'âš ï¸ Deploy Netlify fallito'
  },
  details: {
    githubCommit: githubResponse?.commit?.sha || 'N/A',
    netlifyDeploy: netlifyResponse?.deployId || netlifyResponse?.id || 'N/A',
    netlifySiteId: netlifyResponse?.siteId || netlifyResponse?.site_id || 'N/A',
    deployUrl: netlifyResponse?.deployUrl || 'N/A',
    deploySuccess: netlifyResponse?.success || false
  },
  summary: {
    dataUpdated: githubResponse ? true : false,
    siteDeployed: netlifyResponse?.success || false,
    totalSteps: 'Scraping â†’ GitHub (serie-c-stats.json) â†’ Netlify â†’ Complete'
  }
};

console.log('ğŸŠ WORKFLOW COMPLETATO!');
console.log('ğŸ“Š GitHub commit:', result.details.githubCommit);
console.log('ğŸš€ Netlify deploy ID:', result.details.netlifyDeploy);
console.log('ğŸ—ï¸ Netlify site ID:', result.details.netlifySiteId);
console.log('ğŸŒ Deploy URL:', result.details.deployUrl);
console.log('ğŸ“ File aggiornato: src/data/stats/serie-c-stats.json');
console.log('âœ… Deploy success:', result.details.deploySuccess);

return [{ json: result }];

// Gestione errori del workflow
const errorData = {
  success: false,
  timestamp: new Date().toISOString(),
  message: 'âŒ Errore durante l\'esecuzione del workflow',
  error: 'Dati non validi o scraping fallito',
  suggestion: 'Verificare il sito PlayBasket e la struttura HTML',
  troubleshooting: {
    checkWebsite: 'Verifica che il sito PlayBasket.it sia raggiungibile',
    checkStructure: 'Controlla se la struttura HTML della classifica Ã¨ cambiata',
    checkCredentials: 'Verifica le credenziali GitHub e Netlify',
    checkFile: 'Verifica che il file src/data/stats/serie-c-stats.json esista'
  }
};

console.error('âŒ WORKFLOW FALLITO - Dati non validi');
console.error('ğŸ” Suggerimenti troubleshooting:', JSON.stringify(errorData.troubleshooting, null, 2));

return [{ json: errorData }];
