// Aggiornamento diretto del file TypeScript con dati fissi
// Usiamo i dati che sappiamo essere corretti dal file JSON già aggiornato

// Facciamo una richiesta per ottenere i dati aggiornati
const https = require('https');
const url = 'https://raw.githubusercontent.com/magiaslab/svbtailwindok3/master/src/data/stats/serie-c-stats.json';

return new Promise((resolve, reject) => {
  https.get(url, (res) => {
    let data = '';

    res.on('data', (chunk) => {
      data += chunk;
    });

    res.on('end', () => {
      try {
        const statsData = JSON.parse(data);

        // Creazione contenuto TypeScript
        const tsContent = `// Dati delle statistiche Serie C
// Questo file viene importato direttamente dai componenti
// Aggiornato automaticamente: ${new Date().toISOString()}

export const serieCStatsData = ${JSON.stringify(statsData, null, 2)};`;

        const base64Content = Buffer.from(tsContent).toString('base64');

        // SHA del file corrente (dobbiamo ottenerlo)
        const result = {
          statsData: statsData,
          tsContent: tsContent,
          base64Content: base64Content,
          teamsCount: statsData.standings.length
        };

        console.log(`🎉 Dati preparati: ${statsData.standings.length} squadre`);
        resolve([{ json: result }]);

      } catch (error) {
        console.error('❌ Errore parsing JSON:', error);
        reject(error);
      }
    });
  }).on('error', (error) => {
    console.error('❌ Errore richiesta:', error);
    reject(error);
  });
});

