📊 File aggiornato: ${githubResponse.content ? '✅ Successo' : '❌ Errore'}
📝 Commit SHA: ${githubResponse.commit ? githubResponse.commit.sha : 'N/A'}
📁 File size: ${githubResponse.content ? githubResponse.content.size : 'N/A'} bytes
⏰ Timestamp: ${new Date().toISOString()}`;

console.log(message);

return [{
  json: {
    success: true,
    message: message,
    githubResponse: githubResponse,
    timestamp: new Date().toISOString()
  }
}];
