// Workflow semplificato per aggiornare il file TypeScript
// Ottiene i dati JSON e prepara il contenuto TS in un solo nodo

const https = require('https');

// URL del file JSON aggiornato
const jsonUrl = 'https://raw.githubusercontent.com/magiaslab/svbtailwindok3/master/src/data/stats/serie-c-stats.json';

// URL per ottenere info sul file TS corrente
const tsInfoUrl = 'https://api.github.com/repos/magiaslab/svbtailwindok3/contents/src/data/stats-data.ts';

console.log('ğŸ”„ Inizio aggiornamento file TypeScript');

// Funzione per fare richieste HTTPS
function httpsGet(url, headers = {}) {
  return new Promise((resolve, reject) => {
    const options = {
      headers: {
        'User-Agent': 'N8N-Workflow',
        ...headers
      }
    };

    https.get(url, options, (res) => {
      let data = '';
      res.on('data', (chunk) => data += chunk);
      res.on('end', () => {
        try {
          if (res.headers['content-type']?.includes('application/json')) {
            resolve(JSON.parse(data));
          } else {
            resolve(data);
          }
        } catch (error) {
          reject(error);
        }
      });
    }).on('error', reject);
  });
}

try {
  // 1. Ottieni i dati delle statistiche
  console.log('ğŸ“¥ Scaricamento dati JSON...');
  const statsData = await httpsGet(jsonUrl);

  if (!statsData || !statsData.standings) {
    throw new Error('Dati statistiche non validi');
  }

  console.log(`âœ… Dati ottenuti: ${statsData.standings.length} squadre`);

  // 2. Ottieni info sul file TS corrente (per SHA)
  console.log('ğŸ“¥ Ottenimento SHA file TS...');
  const tsFileInfo = await httpsGet(tsInfoUrl, {
    'Authorization': 'token YOUR_GITHUB_TOKEN_HERE' // Sostituire con token reale
  });

  if (!tsFileInfo || !tsFileInfo.sha) {
    throw new Error('Impossibile ottenere SHA del file TS');
  }

  console.log(`âœ… SHA ottenuto: ${tsFileInfo.sha}`);

  // 3. Crea contenuto TypeScript
  const tsContent = `// Dati delle statistiche Serie C
// Questo file viene importato direttamente dai componenti
// Aggiornato automaticamente: ${new Date().toISOString()}

export const serieCStatsData = ${JSON.stringify(statsData, null, 2)};`;

  // 4. Codifica in Base64
  const base64Content = Buffer.from(tsContent).toString('base64');

  // 5. Prepara dati per GitHub API
  const githubData = {
    message: `Aggiornamento automatico stats-data.ts - ${new Date().toISOString()}`,
    content: base64Content,
    sha: tsFileInfo.sha
  };

  console.log('ğŸ”§ Dati preparati per GitHub:', {
    messageLength: githubData.message.length,
    contentLength: githubData.content.length,
    sha: githubData.sha,
    teamsCount: statsData.standings.length
  });

  return [{ json: githubData }];

} catch (error) {
  console.error('âŒ Errore:', error);
