console.log('ğŸ” Debug - Analisi input data:');
console.log('- Items length:', items ? items.length : 'undefined');
console.log('- Items type:', typeof items);

if (items && items.length > 0) {
  const item = items[0];
  console.log('- Item[0] keys:', Object.keys(item));
  console.log('- Item[0] type:', typeof item);

  // Controlla tutte le possibili proprietÃ 
  const possibleProps = ['data', 'json', 'binary', 'body', 'response'];
  possibleProps.forEach(prop => {
    if (item[prop]) {
      console.log(`- Found property '${prop}':`, typeof item[prop]);
      if (typeof item[prop] === 'string') {
        console.log(`- ${prop} length:`, item[prop].length);
        console.log(`- ${prop} preview:`, item[prop].substring(0, 200));
      }
    }
  });
