// Prova ad accedere ai dati HTML
let htmlData = '';
let accessMethod = 'none';

if (items && items.length > 0) {
  const item = items[0];

  // Metodo 1: item.data
  if (item.data && typeof item.data === 'string') {
    htmlData = item.data;
    accessMethod = 'item.data';
  }
  // Metodo 2: item.json (se Ã¨ stringa)
  else if (item.json && typeof item.json === 'string') {
    htmlData = item.json;
    accessMethod = 'item.json';
  }
  // Metodo 3: item direttamente
  else if (typeof item === 'string') {
    htmlData = item;
    accessMethod = 'item';
  }
  // Metodo 4: item.body
  else if (item.body && typeof item.body === 'string') {
    htmlData = item.body;
    accessMethod = 'item.body';
  }
}

console.log('ğŸ¯ Risultati accesso HTML:');
console.log('- Access method:', accessMethod);
console.log('- HTML length:', htmlData.length);
console.log('- HTML type:', typeof htmlData);

// Test parsing della tabella
let tableFound = false;
let tableContent = '';

if (htmlData && htmlData.length > 0) {
  // Cerca la tabella con regex
  const tableRegex = /<table[^>]*class=["']league_standings_ranking stats["'][^>]*>([\s\S]*?)<\/table>/g;
  const tableMatch = tableRegex.exec(htmlData);

  if (tableMatch) {
    tableFound = true;
    tableContent = tableMatch[1].substring(0, 500); // Prime 500 caratteri
    console.log('âœ… Tabella trovata!');
  } else {
    console.log('âŒ Tabella NON trovata');
    // Cerca pattern alternativi
    const altPatterns = [
      /league_standings_ranking/g,
      /stats/g,
      /<table/g
    ];

    altPatterns.forEach((pattern, index) => {
      const matches = htmlData.match(pattern);
      console.log(`- Pattern ${index + 1} matches:`, matches ? matches.length : 0);
    });
  }
}

return [{
  json: {
    debug: {
      itemsLength: items ? items.length : 0,
      accessMethod: accessMethod,
      htmlLength: htmlData.length,
      htmlType: typeof htmlData,
      tableFound: tableFound,
      htmlPreview: htmlData.substring(0, 300),
      tablePreview: tableContent
    },
    success: htmlData.length > 0,
    message: htmlData.length > 0 ? 'HTML data accessed successfully' : 'Failed to access HTML data'
  }
}];
