// Test semplice per verificare l'accesso ai dati
console.log('ğŸ§ª === TEST ACCESSO DATI ===');

let htmlData = '';
let accessMethod = 'none';

if (items && items.length > 0) {
  const item = items[0];
  console.log('ğŸ“¥ Item ricevuto:', typeof item, Object.keys(item || {}));

  // Test accesso dati (responseFormat: string)
  if (item.json && typeof item.json === 'string' && item.json.length > 1000) {
    htmlData = item.json;
    accessMethod = 'item.json (string)';
  } else if (item.data && typeof item.data === 'string' && item.data.length > 1000) {
    htmlData = item.data;
    accessMethod = 'item.data';
  } else if (item.json && item.json.data && typeof item.json.data === 'string' && item.json.data.length > 1000) {
    htmlData = item.json.data;
    accessMethod = 'item.json.data';
  }
}

// Test parsing tabella
let tableFound = false;
let teamsFound = 0;

if (htmlData && htmlData.length > 1000) {
  const tableRegex = /<table[^>]*class=["']league_standings_ranking stats["'][^>]*>([\s\S]*?)<\/table>/i;
  const tableMatch = htmlData.match(tableRegex);

  if (tableMatch) {
    tableFound = true;
    const rowRegex = /<tr[^>]*>([\s\S]*?)<\/tr>/gi;
    const rows = [];
    let match;

    while ((match = rowRegex.exec(tableMatch[1])) !== null) {
      rows.push(match[1]);
    }

    // Conta squadre (esclude header)
    teamsFound = Math.max(0, rows.length - 1);
  }
}

const testResult = {
  timestamp: new Date().toISOString(),
  connection: {
    success: htmlData.length > 0,
    htmlLength: htmlData.length,
    accessMethod: accessMethod
  },
  parsing: {
    tableFound: tableFound,
    teamsFound: teamsFound,
    success: tableFound && teamsFound > 5
  },
  overall: {
    success: htmlData.length > 1000 && tableFound && teamsFound > 5,
    message: htmlData.length > 1000 && tableFound && teamsFound > 5 ? 
      `âœ… Test superato: ${teamsFound} squadre trovate` : 
      'âŒ Test fallito'
  }
};

console.log('ğŸ¯ Risultato test:', testResult);

return [{ json: testResult }];
