// === DEBUG CELLE HTML ===
console.log('ðŸ” Debug celle HTML...');

// Accesso ai dati HTML
let htmlData = '';
if (items && items.length > 0) {
  const item = items[0];

  if (item.json && item.json.data && typeof item.json.data === 'string') {
    htmlData = item.json.data;
  } else if (item.json && typeof item.json === 'string') {
    htmlData = item.json;
  } else if (item.data && typeof item.data === 'string') {
    htmlData = item.data;
  }
}

if (!htmlData || htmlData.length < 1000) {
  return [{ json: { error: 'HTML not found' } }];
}

// Trova la tabella
const tableRegex = /<table[^>]*class=["']league_standings_ranking stats["'][^>]*>([\s\S]*?)<\/table>/i;
const tableMatch = htmlData.match(tableRegex);

if (!tableMatch) {
  return [{ json: { error: 'Table not found' } }];
}

// Estrai le prime 3 righe per debug
const rowRegex = /<tr[^>]*>([\s\S]*?)<\/tr>/gi;
const rows = [];
let match;
let rowCount = 0;

while ((match = rowRegex.exec(tableMatch[1])) !== null && rowCount < 3) {
  rows.push(match[1]);
  rowCount++;
}

const debugData = {
  timestamp: new Date().toISOString(),
  totalRowsFound: rowCount,
  rows: []
};

// Analizza ogni riga
rows.forEach((rowContent, index) => {
  const cellRegex = /<td[^>]*>([\s\S]*?)<\/td>/gi;
  const cells = [];
  let cellMatch;

  while ((cellMatch = cellRegex.exec(rowContent)) !== null) {
    cells.push(cellMatch[1]);
  }

  debugData.rows.push({
    rowIndex: index,
    isHeader: rowContent.includes('<th'),
    cellCount: cells.length,
    cells: cells.map((cell, cellIndex) => ({
      index: cellIndex,
      rawHtml: cell.substring(0, 200), // Prime 200 caratteri
      cleanText: cell.replace(/<[^>]*>/g, '').replace(/\s+/g, ' ').trim(),
      hasLink: cell.includes('href='),
      hasSpan: cell.includes('<span'),
      hasHighlighted: cell.includes('highlighted_data')
    }))
  });
});

console.log('ðŸŽ¯ Debug risultati:', debugData);
return [{ json: debugData }];
